import React, { Component } from "react";
import PropTypes from "prop-types";
import sary from './sary.png'
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import PropAtelier from "../propAtelier/propAtelier"

class Dashboard extends Component {


onLogoutClick = e => {
  e.preventDefault();
  this.props.logoutUser();
};
  render() {
    console.log('localStorage.local sur dashbord'+localStorage.id);

    const { user } = this.props.auth;

    return (
      <div>
      <div className="row">
      <img href={sary}/>
      </div>
      <div style={{ height: "75vh" }} className="container valign-wrapper">
        <div className="container-fluid">

      </div>
        <div className="row">
          <div className="landing-copy col s12 center-align">
            <h4>
              <b>bonjour,</b> {user.nom.split(" ")[0]}
              <p className="flow-text grey-text text-darken-1">
                Vous avez connecter{" "}
                <span style={{ fontFamily: "monospace" }}>PLAY</span> app üëè

              </p>
            </h4>
            <button
              style={{
                width: "150px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "1rem"
              }}
              onClick={this.onLogoutClick}
              className="btn btn-large waves-effect waves-light hoverable blue accent-3"
            >
              Se Quitter
            </button>
          </div>
        </div>
      </div>
      <PropAtelier/>
      </div>
    );
  }
}

Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { logoutUser }
)(Dashboard);
